-- Trigger for INSERT: Set courseId from topic
DROP TRIGGER IF EXISTS questions_insert_sync_courseId;
CREATE TRIGGER questions_insert_sync_courseId
BEFORE INSERT ON questions
FOR EACH ROW
BEGIN
  SET NEW.courseId = (SELECT courseId FROM topics WHERE id = NEW.topicId);
END;

